import React, { useState } from 'react';
import './Styles.css';

const ContactPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send formObject to the server using fetch or other methods
    // For this example, we will use fetch to send the data to the server
    fetch('/submit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    })
      .then(response => response.json())
      .then(data => {
        // Handle the server response if needed
        console.log(data);
      })
      .catch(error => {
        // Handle errors here
        console.error('Error:', error);
      });
  };

  return (
    <div className="contact-page" id='Connect'>
        
      <div className="contact-content">
        <h2 className="contact-title">Contact us</h2>
        <p className="contact-description">
          Have a question or want to discuss a project? I'm here to help! Feel
          free to reach out to me using the contact information provided below
          or by filling out the form on the right.
        </p>
        <div className="contact-details">
          <h3 className="details-title">Contact Information</h3>
          <ul className="details-list">
            <li className="details-item">
              <i className="fas fa-envelope"></i> Email:
              example@example.com
            </li>
            <li className="details-item">
              <i className="fas fa-phone"></i> Phone: +91-87000-45007
            </li>
            
          </ul>
        </div>
      </div>
      <div className="contact-form">
        <h3 className="form-title">Get In Touch</h3>
        <form className="form">
          <input 
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
          placeholder="Your Name" className="form-input" />
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            placeholder="Your Email"
            className="form-input"
          />
          <textarea
            id="message"
            name="message"
            rows="4"
            value={formData.message}
            onChange={handleChange}
            required
            placeholder="Your Message"
            className="form-textarea"
          ></textarea>
          <button type="submit" className="form-button">
            Send Message
          </button>
        </form>
      </div>
    </div>
  );
};

export default ContactPage;
