import React, { useState } from 'react';
import Draggable from 'react-draggable';

const DraggableContainer = () => {
  const [components, setComponents] = useState([
    { id: 1, content: 'Component 1', x: 100, y: 100 },
    { id: 2, content: 'Component 2', x: 200, y: 200 },
    { id: 3, content: 'Component 3', x: 300, y: 300 },
  ]);

  const handleDrag = (index, newX, newY) => {
    setComponents(prevComponents =>
      prevComponents.map((component, i) =>
        i === index ? { ...component, x: newX, y: newY } : component
      )
    );
  };

  return (
    <div className="draggable-container">
      {components.map((component, index) => (
        <Draggable
          key={component.id}
          defaultPosition={{ x: component.x, y: component.y }}
          onStop={(e, data) => handleDrag(index, data.x, data.y)}
        >
          <div className="draggable-component">{component.content}</div>
        </Draggable>
      ))}
    </div>
  );
};

export default DraggableContainer;
