import React from 'react';

const ProjectCard = ({ title, description, technologies }) => {
  return (
    <div className="project-card">
      <h3>{title}</h3>
      <p>{description}</p>
      <p>Technologies used: {technologies}</p>
    </div>
  );
};

const ProjectShowcase = () => {
  const projects = [
    {
      title: 'CitiBankInnovation Challenge - Fullstack/Blockchain',
      description:
        "Crafted a groundbreaking solution to decentralized micro lending, enabling banks to serve a broader customer base and boost profitability. Leveraged blockchain technology to minimize risk and maximize income.",
      technologies: 'Fullstack, Blockchain',
    },
    {
      title: 'Detection of Alzheimer\'s on the basis of MRI - Machine Learning/Data Analysis',
      description:
        'Developed an innovative system for the early detection of Alzheimer\'s disease by analyzing MRI brain scans. Implemented machine learning algorithms to identify subtle patterns and biomarkers associated with the disease, enabling early intervention and improved patient outcomes.',
      technologies: 'Machine Learning, Data Analysis',
    },
    {
      title: 'Etheronite - Fullstack/Blockchain',
      description:
        'Pioneered an alternative banking system powered by blockchain technology. Etheronite facilitates secure transactions and empowers individuals and businesses to explore the potential of blockchain. With this platform, we aim to make blockchain the new standard for financial transactions and fundraising, driving a future of decentralized finance.',
      technologies: 'Fullstack, Blockchain',
    },
    {
        title: 'Etheronite - Fullstack/Blockchain',
        description:
          'Pioneered an alternative banking system powered by blockchain technology. Etheronite facilitates secure transactions and empowers individuals and businesses to explore the potential of blockchain. With this platform, we aim to make blockchain the new standard for financial transactions and fundraising, driving a future of decentralized finance.',
        technologies: 'Fullstack, Blockchain',
      },
  ];

  return (
    <section className="project-showcase">
      <h2>Project Showcase</h2>
      {projects.map((project, index) => (
        <ProjectCard
          key={index}
          title={project.title}
          description={project.description}
          technologies={project.technologies}
        />
      ))}
    </section>
  );
};

export default ProjectShowcase;
