import React from 'react';
import './Styles.css';

const HeroSection = () => {
  return (
    <section  className="hero-section">
      <div className="hero-content">
        <h1 className="hero-title">MUDIT MOHLA SHARMA</h1>
        <div className="text-loop">
          <span className="looped-text">Engineer | UI/UX Designer | Full Stack Developer | MERN Developer | Blockchain | Python Enthusiast | ML AI Engineer</span>
          
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
