import React, { useState } from 'react';

const AboutSection = () => {
  const [showColumn1, setShowColumn1] = useState(true);
  const [showColumn2, setShowColumn2] = useState(false);

  const handleToggle = (column) => {
    if (column === 'column1') {
      setShowColumn1(true);
      setShowColumn2(false);
    } else if (column === 'column2') {
      setShowColumn1(false);
      setShowColumn2(true);
    }
  };

  return (
    <section className="about-section">
      <div className="about-toggle-bar">
        <button
          className={`toggle-button ${showColumn1 ? 'active' : ''}`}
          onClick={() => handleToggle('column1')}
        >
          Column 1
        </button>
        <button
          className={`toggle-button ${showColumn2 ? 'active' : ''}`}
          onClick={() => handleToggle('column2')}
        >
          Column 2
        </button>
      </div>

      <div className="about-columns">
        {showColumn1 && (
          <div className="about-column">
            <h2>Column 1</h2>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla nec ligula ac metus dapibus iaculis.
              Mauris lacinia efficitur luctus. Donec in justo vel massa tincidunt rhoncus. Quisque nec nulla mauris.
            </p>
          </div>
        )}

        {showColumn2 && (
          <div className="about-column">
            <h2>Column 2</h2>
            <p>
              Fusce consequat lectus vel sapien convallis consequat. Aliquam non metus sed magna bibendum malesuada ac vitae ligula.
              Aenean mattis purus ac arcu euismod tincidunt. Vivamus elementum dui tellus, ut semper mi pellentesque nec.
            </p>
          </div>
        )}
      </div>
    </section>
  );
};

export default AboutSection;
