import React, { useState } from 'react';
import Confetti from 'react-confetti';

import './Styles.css'
const AboutSection = () => {
  const [confettiActive, setConfettiActive] = useState(false);
  const [clickCount, setClickCount] = useState(0);
  const [cursorImage, setCursorImage] = useState(false);

  useEffect(() => {
    if (clickCount >= 10) {
      setCursorImage(true);
    }
  }, [clickCount]);

  const handleConfetti = () => {
    setConfettiActive(true);
    setClickCount(prevCount => prevCount + 1);
    setTimeout(() => {
      setConfettiActive(false);
    }, 2000); // Change the duration as desired
  };

  const handleMouseEnter = () => {
    if (cursorImage) {
      document.body.style.cursor = 'url(path-to-your-image), auto';
    }
  };

  const handleMouseLeave = () => {
    if (cursorImage) {
      document.body.style.cursor = 'default';
    }
  };

  return (
    <section className="about-section" >
      <div className='confetti'>
      {confettiActive && (
        <Confetti
          width={window.innerWidth}
          height={window.innerHeight}
          recycle={false}
        />
      )}
      <div className="about-content">
        <h2>About <i>Me.</i></h2>
        <p>
        A <b>21 year old</b> highly caffeinated and code-driven professional with a wizardry for transforming coffee into code and pizzas into pixel-perfect websites. I craft digital masterpieces that make unicorns jealous, while embarking on epic meme adventures and solving the biggest mystery of all time.</p>

        <p>Let's collaborate in a dynamic and magical environment, where dreams turn into functioning websites. Together, we'll make the web a better place, one line of quirky code at a time!"</p>

        <div ontouchstart="">
  <div class="button">
    <a type='button' onClick={handleConfetti}>Click me</a>
  </div>
</div>
      </div>
      </div>

    </section>
  );
};

export default AboutSection;
