import React, { useState } from 'react';

const InteractiveSection = () => {
  const [smileyCount, setSmileyCount] = useState(0);

  const handleSmileyClick = () => {
    setSmileyCount(smileyCount + 1);
  };

  const renderSmileys = () => {
    const smileys = [];

    for (let i = 0; i < smileyCount; i++) {
      smileys.push(
        <img
          key={i}
          className="smiley"
          src="/path/to/smiley.png"
          alt="Smiley"
        />
      );
    }

    return smileys;
  };

  return (
    <section className="interactive-section">
      <h2>Interactive Section</h2>
      <div className="counter">
        <span className="click-me" onClick={handleSmileyClick}>
          Click Me!
        </span>
        {renderSmileys()}
      </div>
    </section>
  );
};

export default InteractiveSection;
