import React, { useState } from 'react';
import Confetti from 'react-confetti';

import './Styles.css'
const AboutSection = () => {
  const [confettiActive, setConfettiActive] = useState(false);

  const handleConfetti = () => {
    setConfettiActive(true);
    setTimeout(() => {
      setConfettiActive(false);
    }, 2000); // Change the duration as desired
  };

  return (
    <section className="about-section" onMouseEnter={handleMouseEnter}>
      <div className="about-content">
        <h2>About <i>Me.</i></h2>
        <p>
        A <b>21 year old</b> highly caffeinated and code-driven professional with a wizardry for transforming coffee into code and pizzas into pixel-perfect websites. Armed with discipline from my previous life as a secret agent, my code marches to the beat of perfection.</p>

        <p>I craft digital masterpieces that make unicorns jealous, while embarking on epic meme adventures and solving the biggest mystery of all time.</p>

        <p>Let's collaborate in a dynamic and magical environment, where dreams turn into functioning websites. Together, we'll make the web a better place, one line of quirky code at a time!"</p>

        <div ontouchstart="">
  <div class="button">
    <a >Click me</a>
  </div>
</div>
      </div>
    </section>
  );
};

export default AboutSection;
